package uripractice;

import java.util.Scanner;

public class Difference {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

			Scanner scan = new Scanner(System.in);
			
			int A = scan.nextInt();
			int B = scan.nextInt();
			int C = scan.nextInt();
			int D = scan.nextInt();
			
			int diff = ((A * B - C * D));
			
			System.out.println("DIFERENCA = " + diff);
			
			
			
			 
	}

}
